import styled, {css} from 'styled-components';

import { LocationOn, Cake} from '../../styles/Icon'

export const Container = styled.div`
    display: flex;
    flex-direction: column;

    max-height: 100%;
    overflow-y: auto;
    /* confi pro firefox e pro chorme n exibir barra de rolagem */
    scrollbar-width: none;
    ::-webkit-scrollbar{
        display:none;
    }

`;
export const Banner = styled.div`
    /* faz com que ele não encolha: */
    flex-shrink: 0; 

    width: 100%;
    /* 199px o valor minimo que ele atinge e 33vh é 33% da tela */
    height: min(33vw, 199px);

    background: var(--twitter);

    position: relative;
`;
export const Avatar = styled.div`
    width: max(45px, min(135px, 22vw));
    height: max(45px, min(135px, 22vw));

    border: 3.74px solid var(--primary);

    background: var(--gray);
    border-radius: 50%;

    /* o absolute só funciona se o pai tiver o position relative */
    position: absolute;
    bottom: max(-60px, -10vw);
    left: 15px;
`;

export const ProfileData = styled.div`

    padding: min(calc(10vh + 7px), 67px) 16px 0;

    display: flex;
    flex-direction: column;

    >h1{
        font-weight: bold;
        font-size: 19px;
    }

    >h2{
        font-weight: normal;
        font-size: 15px;
    }
`;

const iconCSS = css`
    width: 20px;
    height: 20px;

    color: var(--gray);
`;

export const LocationIcon = styled(LocationOn)`
${iconCSS}`;
export const CakeIcon = styled(Cake)`
${iconCSS}`;


export const Followage = styled.div``;
